mapper.select:


     and project_bianhao = #{projectBianhao}
     and project_name like concat('%', #{projectName}, '%')
     and project_leader = #{projectLeader}
     and dept_name like concat('%', #{deptName}, '%')
     and project_grant = #{projectGrant}
     and school_real_budget = #{schoolRealBudget}
     and project_status = #{projectStatus}
     and project_year = #{projectYear}
     and project_performance = #{projectPerformance}
     and start_data like concat('%', #{startData}, '%')
     and end_data like concat('%', #{endData}, '%')
     and project_type = #{projectType}
     and xiadadanwei = #{xiadadanwei}
     and waibo = #{waibo}
     and hezuofangshi = #{hezuofangshi}
     and level = #{level}
     and create_by = #{createBy}
     and create_time like concat('%', #{createTime}, '%')

order by  start_data desc

SELECT p.project_bianhao projectBianhao,p.project_leader projectLeader,p.project_status projectStatus,j.jixiao_money jixiaoMoney,g.grant_pici grantPici,g.grant_time grantTime
FROM kjc_project p
LEFT JOIN kjc_jixiao j
ON j.project_bianhao = p.project_bianhao
LEFT JOIN kjc_grant g
ON g.project_bianhao = p.project_bianhao

     and p.project_id = ${projectId}
     and p.project_bianhao = #{projectBianhao}
     and p.project_name like concat('%', #{projectName}, '%')
     and p.project_leader = #{projectLeader}
     and p.dept_name like concat('%', #{deptName}, '%')
     and p.project_grant = #{projectGrant}
    and p.school_real_budget=#{schoolRealBudget}
     and p.project_year = #{projectYear}
     and p.project_performance = #{projectPerformance}
     and p.project_status = #{projectStatus}
     and p.start_data like concat('%', #{startData}, '%')
     and p.end_data like concat('%', #{endData}, '%')
     and p.project_type = #{projectType}
     and p.xiadadanwei = #{xiadadanwei}
     and p.waibo = #{waibo}
     and p.hezuofangshi = #{hezuofangshi}
     and p.level = #{level}
     and p.create_by = #{createBy}
     and p.create_time like concat('%', #{createTime}, '%')

order by  start_data

mapper.update:
update kjc_project
project_bianhao = #{projectBianhao}, project_name = #{projectName}, project_leader = #{projectLeader}, dept_name = #{deptName}, project_grant = #{projectGrant}, school_real_budget = #{schoolRealBudget}, project_status = #{projectStatus}, project_year = #{projectYear}, project_performance = #{projectPerformance}, start_data = #{startData}, end_data = #{endData}, project_type = #{projectType}, xiadadanwei = #{xiadadanwei}, waibo = #{waibo}, hezuofangshi = #{hezuofangshi}, level = #{level}, create_by = #{createBy}, create_time = #{createTime},
where project_id = #{projectId}
